import React, { useState, useEffect } from 'react'
import { useHistory, useLocation } from 'react-router-dom'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow,
  CButton,
  CModal,
  CModalTitle,
  CModalHeader,
  CModalBody,
  CForm,
  CInputGroupPrepend,
  CInputGroupText,
  CInputGroup,
  CInput,
  CFormGroup,
  CInputCheckbox,
  CLabel,
  CModalFooter,
  CBadge
} from '@coreui/react'

import DataTable from "react-data-table-component";
import DataTableExtensions from "react-data-table-component-extensions";
import Select from 'react-select';
import CIcon from '@coreui/icons-react';
import dataservice from '../../service/dataservice';
import { toast } from 'react-toastify';
import { CSVLink } from "react-csv";




const notify = (type, message, systemerror) => {
  switch (type) {
    case 1:
      toast.success(message, {
        position: toast.POSITION.TOP_CENTER
      });
      break;
    case 2:
      var dtlmsg;
      if (systemerror !== undefined) {
        dtlmsg = <div>Please contact to administrator!<br></br>{message}</div>;
      } else {
        dtlmsg = <div>{message}</div>;
      }

      toast.error(<div>
        <CIcon name="cil-warning" /> Error

        {dtlmsg}

      </div>, {
        position: toast.POSITION.TOP_CENTER
      });
      break;

    default:
      break;
  }
}
const headerss = [
  { label: "Transaction id", key: "paymentreferencenumber" },
  { label: "Primary Contact Name", key: "primaryguest" },
  { label: "Primary Contact Email", key: "email" },
  { label: "Mobile Number", key: "mobilenumber" },
  { label: "Guest List", key: "other" },
  { label: "Hotel", key: "hotel" },
  { label: "From Date", key: "fromdate" },
  { label: "TO Date", key: "todate" },
  { label: "Number Of Tourist", key: "pax" },
  { label: "Time", key: "slotid" },
  { label: "Vehicle Parking", key: "vehicle" }

];

const TransactionLog = () => {
  const [csvdata, setCSVData] = useState([])
  var url = "https://maubantourism.smartpay.ph/tourbookingphp/tempdata/";
  //var url="https://tourism-test.smartpay.ph/tourbookingphp/tempdata/";
  const gettransactiondata = () => {
    dataservice("report", { requestfor: 'transactionlog', data: { flag: 'a' } })
      .then(function (data) {
        if (data.resultkey == 1) {
          var result = data.resultvalue;

          setTransactionLog({ ...TransactionLogdata, ["data"]: result });
          if (result.length > 0) {
            let datasd = [];
            for (let index = 0; index < result.length; index++) {
              const element = result[index];
              datasd.push(
                {
                  paymentreferencenumber: element.paymentreferencenumber,
                  primaryguest: element.primaryguest,
                  email: element.email,
                  mobilenumber: element.mobilenumber,
                  other: element.other,
                  hotel: element.hotel,
                  fromdate: element.fromdate,
                  todate: element.todate,
                  pax: element.pax,
                  buybracelet: element.pax,
                  rentbracelet: element.pax,
                  slotid: element.slotid,
                  vehicle: element.vehicle
                }
              );

            }
            setCSVData(datasd);
          }

        } else {
          //  notify(2,"No data found");
        }

      })
      .catch(function (edata) {
        notify(2, edata.toString(), 1);
      });



  }
  useEffect(() => {

    gettransactiondata();


  }, [])






  const columns = [
    {
      name: 'Transaction id',
      selector: 'paymentreferencenumber',
      sortable: true,
    },
    {
      name: 'Primary Contact Name',
      selector: 'primaryguest',
      sortable: true,
    },
    {
      name: 'Primary Contact Email',
      selector: 'email',
      sortable: true,
    },
    {
      name: 'Mobile Number',
      selector: 'mobilenumber',
      sortable: true,
    },
    {
      name: 'Guest List',
      selector: 'other',
      sortable: true,
    },
    {
      name: 'Hotel',
      selector: 'hotel',
      sortable: true,
    },
    {
      name: 'From Date',
      selector: 'fromdate',
      sortable: true,
    },
    {
      name: 'TO Date',
      selector: 'todate',
      sortable: true,
    },
    {
      name: 'Number Of Tourist',
      selector: 'pax',
      sortable: true,
    },
    {
      name: 'Bracelet Buy',
      selector: 'pax',
      sortable: true,
    },
    {
      name: 'Bracelet Rent',
      selector: 'pax',
      sortable: true,
    },
    {
      name: 'Time ',
      selector: 'slotid',
      sortable: true,
    },
    {
      name: 'vehicle',
      selector: 'vehicle',
      sortable: true,
    },

    {
      name: 'qrcode',
      selector: 'qrcode',
      sortable: true,
      cell: d => <img width='50' height='50' src={url + d.qrcode} alt="slide 1" />,


    }
    //,
    // {
    //   name: 'Status',
    //   selector: 'isactive',
    //   sortable: true,
    //   cell: d => <CBadge color={d.isactive=="1"?"success":"danger"}>
    //                       {d.isactive=="1"?"Active":"In Active"}
    //                      </CBadge>,
    // }

  ];
  const data = [];
  const [TransactionLogdata, setTransactionLog] = useState({
    columns,
    data
  });
  return (
    <>
      <CRow>
        <CCol xl={12}>
          <CCard>
            <CCardHeader>
              <CRow>
                <CCol xl={6}>
                  Transaction Log
                  <small className="text-muted"> Report</small>
                </CCol>

              </CRow>



            </CCardHeader>
            <CCardBody>
              <CSVLink className="mr-1 btn btn-primary" style={{ float: "right" }} data={csvdata} headers={headerss} filename={"Transactionlog.csv"}>
                Download csv
              </CSVLink>

              <DataTableExtensions
                {...TransactionLogdata}

              >


                <DataTable
                  defaultSortField="id"
                  defaultSortAsc={false}
                  pagination
                  highlightOnHover

                />
              </DataTableExtensions>

            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    </>

  )
}
export default TransactionLog
