import React, { useState, useEffect, useContext } from "react";
import {
  CWidgetDropdown,
  CRow,
  CCol,
  CDropdown,
  CDropdownMenu,
  CDropdownItem,
  CDropdownToggleGARawData
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import ChartLineSimple from '../charts/ChartLineSimple';
import ChartBarSimple from '../charts/ChartBarSimple';
import dataservice from "../service/dataservice";

const WidgetsDropdown = () => {
  // render
  const [GAmonth, setGAmonth] = useState(0);
  const [Cmonth, setCmonth] = useState(0);
  const [, setGARawData] = useState([]);
  const [GAData, setGAData] = useState([]);

  const getGuestArrived = () => {
    dataservice("dashboardstats", { requestfor: 'getGuestArrived', data: { month: GAmonth } })
      .then(function (data) {
        debugger;
        if (data.resultkey == 1) {
          var _GAdata = [];
          if (data.resultvalue.length > 0) {
            for (let index = 0; index < data.resultvalue.length; index++) {
              const element = data.resultvalue[index];
              _GAdata.push({ cnt: parseFloat(element.count), mnt: element.mnt });
            }
          }
          setGARawData(_GAdata)
          var _GAmonthwiseData = _GAdata.filter(x => x.mnt == GAmonth);


        }
      })
  }
  const getCollection = () => {
    dataservice("dashboardstats", { requestfor: 'getCollection', data: { month: Cmonth } })
      .then(function (data) {

      })
  }

  useEffect(() => {
    debugger;
    getGuestArrived();

  }, [])
  return (
    <CRow>
      <CCol sm="6" lg="3">
        <CWidgetDropdown
          color="gradient-primary"
          header=""
          text="Guest Arrived"
          footerSlot={
            <ChartLineSimple
              pointed
              className="c-chart-wrapper mt-3 mx-3"
              style={{ height: '70px' }}
              dataPoints={[GAData]}
              pointHoverBackgroundColor="primary"
              label="Members"
              labels="months"
            />
          }
        >
          <CDropdown>
            <CDropdownToggle color="transparent">
              {/* <CIcon name="cil-settings" /> */}
            </CDropdownToggle>
            <CDropdownMenu className="pt-0" placement="bottom-end">
              <CDropdownItem>Jan</CDropdownItem>
              <CDropdownItem>Feb</CDropdownItem>
              <CDropdownItem>Mar</CDropdownItem>
              <CDropdownItem>Apr</CDropdownItem>
              <CDropdownItem>May</CDropdownItem>
              <CDropdownItem>Jun</CDropdownItem>
              <CDropdownItem>Jul</CDropdownItem>
              <CDropdownItem>Aug</CDropdownItem>
              <CDropdownItem>Sep</CDropdownItem>
              <CDropdownItem>Oct</CDropdownItem>
              <CDropdownItem>Nov</CDropdownItem>
              <CDropdownItem>Dec</CDropdownItem>

            </CDropdownMenu>
          </CDropdown>
        </CWidgetDropdown>
      </CCol>

      <CCol sm="6" lg="3">
        <CWidgetDropdown
          color="gradient-info"
          header=""
          text="Collection"
          footerSlot={
            <ChartLineSimple
              pointed
              className="mt-3 mx-3"
              style={{ height: '70px' }}
              dataPoints={[1, 18, 9, 17, 34, 22, 11]}
              pointHoverBackgroundColor="info"
              options={{ elements: { line: { tension: 0.00001 } } }}
              label="Members"
              labels="months"
            />
          }
        >
          <CDropdown>
            <CDropdownToggle color="transparent">
              {/* <CIcon name="cil-settings" /> */}
            </CDropdownToggle>
            <CDropdownMenu className="pt-0" placement="bottom-end">
              <CDropdownItem>Jan</CDropdownItem>
              <CDropdownItem>Feb</CDropdownItem>
              <CDropdownItem>Mar</CDropdownItem>
              <CDropdownItem>Apr</CDropdownItem>
              <CDropdownItem>May</CDropdownItem>
              <CDropdownItem>Jun</CDropdownItem>
              <CDropdownItem>Jul</CDropdownItem>
              <CDropdownItem>Aug</CDropdownItem>
              <CDropdownItem>Sep</CDropdownItem>
              <CDropdownItem>Oct</CDropdownItem>
              <CDropdownItem>Nov</CDropdownItem>
              <CDropdownItem>Dec</CDropdownItem>

            </CDropdownMenu>
          </CDropdown>
        </CWidgetDropdown>
      </CCol>



      <CCol sm="6" lg="3">
        <CWidgetDropdown
          color="gradient-danger"
          header=""
          text="Bracelet Stats"
          footerSlot={
