                    </div>
                    {/*<!--Grid column-->*/}
                  </div>
                  {/*<!--Grid row-->*/}
                  {/* <!--Username--> */}

                  <div className="row">
                    <div className="col-lg-6 col-md-12 mb-6">
                      Select Type of Boat
                      <span style={{ color: "red" }}>
