import React, { useState, useEffect, useContext } from "react";
import {
  CWidgetDropdown,
  CRow,
  CCol,
  CDropdown,
  CDropdownMenu,
  CDropdownItem,
  CDropdownToggle,

} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import ChartLineSimple from '../charts/ChartLineSimple';
import ChartBarSimple from '../charts/ChartBarSimple';
import dataservice from "../service/dataservice";
import Select from "react-select";
const WidgetsDropdown = () => {
  // render
  const [GAmonth, setGAmonth] = useState(2);
  const [GAmonthstr, setGAmonthstr] = useState("Feb");
  const [Cmonth, setCmonth] = useState(0);
  const [GARawData, setGARawData] = useState([]);
  const [GAData, setGAData] = useState([]);
  const [GALabel, setGALabel] = useState([]);
  const [Months, setMonths] = useState([{ label: "Jan", value: 1 }, { label: "Feb", value: 2 }, { label: "Mar", value: 3 }, { label: "Apr", value: 4 }, { label: "May", value: 5 }, { label: "Jun", value: 6 },
  { label: "Jul", value: 7 }, { label: "Aug", value: 8 }, { label: "Sep", value: 9 }, { label: "Oct", value: 10 }, { label: "Nov", value: 11 }, { label: "Dec", value: 12 }]);
  let countdata = [];

  const getGuestArrived = (isFetchCurrentMonth, Month) => {
    dataservice("dashboardstats", {
      requestfor: 'getGuestArrived',
      data: { isCurrentMonth: isFetchCurrentMonth, month: Month }
    })
      .then(function (data) {
        debugger;
        if (data.resultkey == 1) {
          var _GARawdata = [];
          var _GAData = [];
          var _GAlabels = [];
          if (data.resultvalue.length > 0) {
            for (let index = 0; index < data.resultvalue.length; index++) {
              const element = data.resultvalue[index];
              _GARawdata.push({ cnt: parseFloat(element.count), mnt: element.mnt, dt: element.dt });
              if (parseFloat(element.mnt) == GAmonth) {
                _GAData.push(parseFloat(element.count));
                _GAlabels.push(element.dt);
              }
            }
          }
          setGARawData(_GARawdata)


          setGAData(_GAData);
          setGALabel(_GAlabels);

        }
      })
  }
  const getCollection = () => {
    dataservice("dashboardstats", { requestfor: 'getCollection', data: { month: Cmonth } })
      .then(function (data) {

      })
  }
  const handleChangeOptionNOG = (e) => {
    // if (bulkuploaddata.length > 0) {
    //   setSelectedNumberGuestBeforConfirm(e.value);
    //   setIsConfirmChange(!isconfirmchange);
    // } else {
    //   setSelectedNumberGuest(e.value);
    // }
    let monthstr = "";
    switch (e.label) {
      case 1:
        monthstr = "Jan";
        break;
      case 2:
        monthstr = "Feb";
        break;
      case 3:
        monthstr = "Mar";
        break;
      case 4:
        monthstr = "Apr";
        break;
      case 5:
        monthstr = "May";
        break;
      case 6:
        monthstr = "Jun";
        break;
      case 7:
        monthstr = "Jul";
        break;
      case 8:
        monthstr = "Aug";
        break;
      case 9:
        monthstr = "Sep";
        break;
      case 10:
        monthstr = "Oct";
        break;
      case 11:
        monthstr = "Nov";
        break;
      case 12:
        monthstr = "Dec";
        break;

      default:
        break;
    }
    setGAmonth(e.value);
    setGAmonthstr(monthstr);
  };
  const handleChangeOptionGA = (id, opt) => {

    switch (opt) {
      case "GA":
        setGAmonth(id);

        setGAmonthstr(monthstr);
        break;

      default:
        break;
    }

  }
  useEffect(() => {
    getGuestArrived(true, 1);

  }, [])
  return (
    <CRow>
      <CCol sm="6" lg="3">
        <CWidgetDropdown
          color="gradient-primary"
          header=""
          text="Guest Arrived"
          footerSlot={
            <ChartLineSimple
              pointed
              className="c-chart-wrapper mt-3 mx-3"
              style={{ height: '70px' }}
              dataPoints={GAData}
              pointHoverBackgroundColor="primary"
              label={GAmonthstr}
              labels={GALabel}
            />
          }
        >
          <CDropdown>
            <CDropdownToggle color="transparent">
              {/* <CIcon name="cil-settings" /> */}
            </CDropdownToggle>
            <CDropdownMenu className="pt-0" placement="bottom-end">


              {(() => {
                const rows = [];
                if (Months.length > 0) {
                  for (let i = 0; i < Months.length; i++) {
                    var _mnt = Months[i];
                    //_veh.checked = false;

                    rows.push(
                      <>
                        <Select
                          className="dropdown"
                          placeholder="Select"
